package Variable;

public class IntegerDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		a = 1;
		System.out.println(a+1); //2
		 
		a = 2;
		System.out.println(a+1); //3

		double aa = 1.1; 
		System.out.println(aa+1.1); // 2.2
		 
		aa = 2.1; 
		System.out.println(aa+1.1); // 3.2

		
		
	}

}
